/**
 * The arguments for the `onChange` ColorPalette event.
 */
/**
 * @hidden
 */
export interface ColorPaletteChangeEvent {
    /**
     * The current value of the ColorPalette.
     */
    value: string;
    /**
     * The current value of the ColorPalette in RGBA format.
     */
    rgbaValue: string;
}
