async function getRubroVta(){
  try{
    let pool = await sql.connect(config);
    let listaRubroVta = await pool.request().query("SELECT dbo.VENT_RUBV.RUBV_RUBRO_VENTA ,dbo.VENT_RUBV.RUBV_NOMBRE FROM dbo.VENT_RUBV WHERE(((dbo.VENT_RUBV.RUBV_RUBRO_VENTA)<>'-' AND (dbo.VENT_RUBV.RUBV_RUBRO_VENTA)<>'0001' AND (dbo.VENT_RUBV.RUBV_RUBRO_VENTA)<>'0002' AND (dbo.VENT_RUBV.RUBV_RUBRO_VENTA)<>'50' AND (dbo.VENT_RUBV.RUBV_RUBRO_VENTA)<>'60'))");
    return listaRubroVta.recordsets;
  }
  catch(error){
    console.log(error);
  }
}

