async function getUltimaVta(){
  try{
    let pool = await sql.connect(config);
    let listaUltimaVta = await pool.request().query("DECLARE @Ultima_Vuelta TABLE(CVCL_FECHA_EMI DATETIME, CVCL_CLIENTE INT, CLIE_NOMBRE VARCHAR(30), CVCL_DIVISION_CVCL SMALLINT, CVCL_SUCURSAL_IMP INT, CVCL_TIPO_VAR VARCHAR(3), CVCL_NUMERO_CVCL DECIMAL(10,0), CVRF_RENGLON_CVRF SMALLINT, ARTS_ARTICULO_EMP VARCHAR(30), ARTS_NOMBRE VARCHAR(70), CVRF_UNIMED VARCHAR(3), CVRF_CANT_ING DECIMAL(14,4), CVRF_PRE_NETLOC_SI DECIMAL(14,4), Subtotal_SI DECIMAL(14,4)) INSERT INTO @Ultima_Vuelta SELECT dbo.CCOB_CVCL.CVCL_FECHA_EMI ,dbo.CCOB_CVCL.CVCL_CLIENTE ,dbo.CCOB_CLIE.CLIE_NOMBRE ,dbo.CCOB_CVCL.CVCL_DIVISION_CVCL ,dbo.CCOB_CVCL.CVCL_SUCURSAL_IMP ,dbo.CCOB_CVCL.CVCL_TIPO_VAR ,dbo.CCOB_CVCL.CVCL_NUMERO_CVCL ,dbo.VENT_CVRF.CVRF_RENGLON_CVRF ,dbo.STOC_ARTS.ARTS_ARTICULO_EMP ,dbo.STOC_ARTS.ARTS_NOMBRE ,dbo.VENT_CVRF.CVRF_UNIMED ,dbo.VENT_CVRF.CVRF_CANT_ING ,dbo.VENT_CVRF.CVRF_PRE_NETLOC_SI ,Round([CVRF_CANT_ING]*[CVRF_PRE_NETLOC_SI],2) AS [Subtotal_SI] FROM(dbo.CCOB_CLIE INNER JOIN (dbo.CCOB_CVCL INNER JOIN dbo.VENT_CVRF ON (dbo.CCOB_CVCL.CVCL_NUMERO_CVCL = dbo.VENT_CVRF.CVRF_NUMERO_CVCL) AND (dbo.CCOB_CVCL.CVCL_TIPO_VAR = dbo.VENT_CVRF.CVRF_TIPO_CVCL) AND (dbo.CCOB_CVCL.CVCL_SUCURSAL_IMP = dbo.VENT_CVRF.CVRF_SUCURSAL_CVCL) AND (dbo.CCOB_CVCL.CVCL_DIVISION_CVCL = dbo.VENT_CVRF.CVRF_DIVISION_CVCL)) ON dbo.CCOB_CLIE.CLIE_CLIENTE = dbo.CCOB_CVCL.CVCL_CLIENTE) INNER JOIN dbo.STOC_ARTS ON dbo.VENT_CVRF.CVRF_ARTICULO = dbo.STOC_ARTS.ARTS_ARTICULO SELECT [@Ultima_Vuelta].ARTS_ARTICULO_EMP, [@Ultima_Vuelta].ARTS_NOMBRE, Max([@Ultima_Vuelta].CVCL_FECHA_EMI) AS [Ultima Vta] FROM @Ultima_Vuelta GROUP BY [@Ultima_Vuelta].ARTS_ARTICULO_EMP, [@Ultima_Vuelta].ARTS_NOMBRE;");
    return listaUltimaVta.recordsets;
  }
  catch(error){
    console.log(error);
  }
}

